import React, { useState, useEffect } from 'react'
import axios from 'axios'

const ProductList = () => {
    const [productList, setProductList] = useState([])
    const [shoppingCart, setShoppingCart] = useState([])

    // Fetch the list of products from the API
    useEffect(() => {
        axios.get('https://fakestoreapi.com/products')
            .then(response => {
                setProductList(response.data);
            })
    }, [])


    // function to add a product to the cart
    function addToCart(product) {
        // check if the product is already in the cart
        if (cart[product.id]) {
            // if it is, update the quantity
            setCart({
                ...cart,
                [product.id]: {
                    ...cart[product.id],
                    quantity: cart[product.id].quantity + 1,
                },
            });
        } else {
            // if it's not, add it to the cart with a quantity of 1
            setCart({ ...cart, [product.id]: { ...product, quantity: 1 } });
        }
    }

    // function to update the quantity of a product in the cart
    function updateQuantity(productId, quantity) {
        setCart({
            ...cart,
            [productId]: {
                ...cart[productId],
                quantity,
            },
        });
    }

    return (
        <div>
            {productList.map(product => (
                <div key={product.id} className="card">
                    <img src={product.image} alt={product.name} />
                    <div class="container">
                        <h3>{product.name}</h3>
                        <h4>{product.title}</h4>
                        <div>${product.price}</div>
                    </div>
                    <select value={product.quantity} onChange={e => updateQuantity(product, e.target.value)}>
                        {[...Array(product.stock).keys()].map(quantity => (
                            <option key={quantity} value={quantity + 1}>
                                {quantity + 1}
                            </option>
                        ))}
                    </select>
                    <button>
                        Remove
                    </button>
                </div>
            ))}
        </div>
    )
}

export default ProductList
