import React, { useState, useEffect } from 'react'
import axios from 'axios'

const ProductList = () => {
    const [productList, setProductList] = useState([])
    const [cart, setCart] = useState([])

    // Fetch the list of products from the API
    useEffect(() => {
        axios.get('https://fakestoreapi.com/products')
            .then(response => {
                setProductList(response.data);
            })
    }, [])


    function updateQuantity(product, quantity) {
        // check if the product is already in the cart
        if (cart[product.id]) {
            // if it is, update the quantity
            setCart({
                ...cart,
                [product.id]: {
                    ...cart[product.id],
                    quantity,
                },
            });
        } else {
            // if it's not, add it to the cart with the selected quantity
            setCart({ ...cart, [product.id]: { ...product, quantity } });
        }
    }

    // function to remove a product from the cart
    function removeFromCart(productId) {
        // create a new cart object without the product
        const newCart = { ...cart };
        delete newCart[productId];
        setCart(newCart);
    }

    // calculate the total price of the products in the cart
  const totalPrice = Object.values(cart).reduce((acc, product) => acc + product.price * product.quantity, 0);

    return (
        <div>
            {productList.map(product => (
                <div key={product.id} className="card">
                    <img src={product.image} alt={product.name} />
                    <div class="container">
                        <h3>{product.name}</h3>
                        <h4>{product.title}</h4>
                        <div>${product.price}</div>
                    </div>
                    <select value={product.quantity} onChange={e => updateQuantity(product, e.target.value)}>
                        {[...Array(10).keys()].map(quantity => (
                            <option key={quantity} value={quantity + 1}>
                                {quantity + 1}
                            </option>
                        ))}
                    </select>
                    <button>
                        Remove
                    </button>
                </div>
            ))}

            <div>
                <h2>Cart Summary</h2>
                {Object.values(cart).map(product => (
                    <div key={product.id}>
                        {product.name}
                        <select value={product.quantity} onChange={e => updateQuantity(product.id, e.target.value)}>
                            {[...Array(product.stock).keys()].map(quantity => (
                                <option key={quantity} value={quantity + 1}>
                                    {quantity + 1}
                                </option>
                            ))}
                        </select>
                        <button onClick={() => removeFromCart(product.id)}>Remove</button>
                    </div>
                ))}
                Shipping: $5
                <br />
                Price: ${totalPrice}
            </div>
        </div>
    )
}

export default ProductList
