import React, { useState, useEffect } from 'react'
import axios from 'axios'

const ProductList = () => {
    const [productList, setProductList] = useState([])
    const [cart, setCart] = useState([])

    // Fetch the list of products from the API
    useEffect(() => {
        axios.get('https://fakestoreapi.com/products')
            .then(response => {
                setProductList(response.data);
            })
    }, [])

    const updateCart = (event) => {
        console.log(event.target.value)
        console.log(event.target.id)
        const id = Number(event.target.id)
        const quantity = Number(event.target.value)
        const productToAdd = productList.find(product => product.id === id)
        const itemAlreadyInCart = cart.find(product => product.id === id)
        console.log(productList)
        console.log(productToAdd)
        console.log("itemAlreadyInCart", itemAlreadyInCart)
        if (!itemAlreadyInCart) {
            setCart(cart => {
                return [...cart, productToAdd]
            })
        }

        if (itemAlreadyInCart) {
            setCart(cart =>
                cart.map(product => {
                    if (product.id === id) {
                        return { ...product, quantity: quantity }
                    }
                }))
        }

        console.log("cart", cart)

        /* if (itemAlreadyInCart)
            setCart(cart => {
                return [...cart, productToAdd]
            }) */
    }

    const removeFromCart = () => { }

    // calculate the total price of the products in the cart
    const totalPrice = cart.reduce((acc, product) => acc + product.price * product.quantity, 0);

    return (
        <div className="shopping-cart">
            <div className="products">
                {productList.map(product => (
                    <div key={product.id} className="card">
                        <img src={product.image} alt={product.name} />
                        <div class="container">
                            <h3>{product.name}</h3>
                            <h4>{product.title}</h4>
                            <div>${product.price}</div>
                            <input id={product.id} type="number" min="1" max="10" onChange={(event) => updateCart(event)}></input>
                        </div>
                    </div>
                ))}
            </div>
            <div className="cart-summary">
                <h2>Cart Summary</h2>
                {cart.map(product => (
                    <div key={product.id}>
                        {product.name}
                        <button onClick={() => removeFromCart(product.id)}>Remove</button>
                    </div>
                ))}
                Shipping: $5
                <br />
                Price: ${totalPrice}
            </div>
        </div>
    )
}

export default ProductList
